// ProcessAlarmBits preenche os valores dos bits de alarme baseado nos dados do PLC
func ProcessAlarmBits(bitExtractor *BitExtractor) *AlarmBitsStructure {
	alarmDefs := GetAlarmBitDefinitions()
	
	// Preencher valores para cada equipamento
	for i := range alarmDefs.Enchimento {
		bit := &alarmDefs.Enchimento[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	for i := range alarmDefs.Esvaziamento {
		bit := &alarmDefs.Esvaziamento[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	for i := range alarmDefs.PortaJusante {
		bit := &alarmDefs.PortaJusante[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	for i := range alarmDefs.PortaMontante {
		bit := &alarmDefs.PortaMontante[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	for i := range alarmDefs.ComandoEclusa {
		bit := &alarmDefs.ComandoEclusa[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	for i := range alarmDefs.EsgotoDrenagem {
		bit := &alarmDefs.EsgotoDrenagem[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	for i := range alarmDefs.ReservaFalhas {
		bit := &alarmDefs.ReservaFalhas[i]
		bit.Value = bitExtractor.GetSingleBit("alarm", bit.WordIndex, bit.BitIndex)
	}
	
	return alarmDefs
}